import React from "react"
import {BrowserRouter, Link, Route} from "react-router-dom";
import Course from "./CourseTable";
import CourseEditorComponent from "./CourseEditorComponent";
import Login from "./Login";
import Register from "./Register";
import Profile from "./Profile";
import CourseNavbar from "./CourseNavBar";


export default class CourseManagerComponent extends React.Component {
    state ={
        value:true,
        courses: []
    }

    handleClick = () => {
      const newValue = !this.state.value
      this.setState({
          value: newValue
      })
    }

    render() {
        return(
            <BrowserRouter>
            <CourseNavbar courses={this.state.courses}/>
            <div className="container">
            <Link to="/login">Login</Link> |
            <Link to="/register">Register</Link> |
            <Link to="/profile">Profile</Link> |
            <Link to="/courses">Courses</Link> |
            <Link to="/edit">Editor</Link> |
              <Route path="/login" exact component={Login}/>
              <Route path="/register" exact component={Register}/>
              <Route path="/profile" exact component={Profile}/>
              <Route path="/courses" exact>
                <button className="btn-info btn-sm" onClick={this.handleClick}>
                    Toggle
                </button>
                {!this.state.value && <CourseGrid courses={this.state.courses}/>}
                {this.state.value && <CourseTable courses={this.state.courses} instructer="Chengcheng" term="2020fall"/>}
              </Route>
              <Route path="/edit/:courseId" exact component={CourseEditorComponent}/>
            </div>  
          </BrowserRouter>
        )
    }
}
