import React from "react";
import {Link, Route} from "react-router-dom";

const courseBeingEdited = false
const editCourse = () => {}
export default class CourseRowComponent extends React.Component{
  state = {
    editing: false
  }
  render() {
    return(
        <tr>
        <td>
        {
          this.state.editing && 
          <input value={courseBeingEdited.title} className="form-control"></input>
        }
        {
          !this.state.editing && 
          <Link to={`/edit/${this.props.course._id}`}>{this.props.course.title}</Link>
        }
        </td>
        <td>{this.props.course.owner}</td>
        <td>{this.props.course.lastUpdated}</td>
        <td>
          <button
            onClick={() => this.props.deleteCourse(this.props.course)} //careful!!!
            className="btn btn-success">
            Delete
          </button>
          <button 
          onClick={() => this.setState({editing : true})}
          className="btn btn-info">Edit</button>
          <button 
          onClick={() => this.setState({editing : true})}
          className="btn btn-info">OK</button>
        </td>
      </tr>
    )
  }

}
