import React from "react"
import {BrowserRouter, Link, Route} from "react-router-dom";
import CourseTable from "./CourseTable";
import CourseGrid from "./CourseGrid"
import CourseEditorComponent from "./CourseEditorComponent";
import Login from "./Login";
import Register from "./Register";
import Profile from "./Profile";
import CourseNavbar from "./CourseNavBar";
import { FaToggleOff } from 'react-icons/fa';


export default class CourseManagerComponent extends React.Component {
    state ={
        value:true,
        courses: []
    }

    handleClick = () => {
      const newValue = !this.state.value
      this.setState({
          value: newValue
      })
    }

  //initialize
  componentDidMount() {
    findAllCourses()
      .then(courses => {
        this.setState({
            courses : courses
          }
        )
      })
  }

  // delete course
  deleteCourse = (course) => {
    //update the state and re-render
    deleteCourse(course._id)
      .then(this.setState(prevState => ({
        courses: prevState.courses.filter(d => d._id !== course._id) //update
      })))
  }

  //add course
  addCourse = (course) => {
    let newCourse = {  
      title: "New Course",
      owner: "me",
      modified: "1/1/2020"
    }

    createCourse(newCourse)
      .then(newCourse => this.setState((prevState) => {
        return {
          courses: [
            ...prevState.courses, newCourse
          ]
        }
    }))
  }
    //edit course
  editCourse = (course) => {
    this.setState({
      courseBeingEdited: course
    })
  }

    render() {
        return(
            <BrowserRouter>
            <CourseNavbar courses={this.state.courses}/>
            <div className="container">
            <Link to="/login">Login</Link> |
            <Link to="/register">Register</Link> |
            <Link to="/profile">Profile</Link> | 
            <Link to="/courses">Courses</Link> | 
              <Route path="/login" exact component={Login}/>
              <Route path="/register" exact component={Register}/>
              <Route path="/profile" exact component={Profile}/>
              <Route path="/courses" exact>
                <FaToggleOff onClick={this.handleClick}/>
                {!this.state.value && <CourseGrid courses={this.state.courses}/>}
                {this.state.value && <CourseTable courses={this.state.courses} instructer="Chengcheng" term="2020fall"/>}
              </Route>
              <Route path="/edit/:courseId" exact component={CourseEditorComponent}/>
            </div>  
          </BrowserRouter>
        )
    }
}
