import React from "react";
import CourseRoleComponent from "./CourseRoleComponent";


/**
 * function: limitation can't embed classes and maintain status
 * 
 * const CourseListComponent = ({ instructer, term }) => (
  <div className="container">
    <h1>
      Course List (For {instructer} at {term}){" "}
    </h1>
    <table className="table">
      {
        course.map((item) => (
          <CourseRoleComponent item={item} />
        )) //iterate the elememnt
      }
    </table>
  </div>
); 
 */


class CourseListComponent extends React.Component {
    // state just like fields, local state instance
    state = {
        course:  [
            {
              name: "cs5610",
              term: "20fall",
            },
            {
              name: "cs5200",
              term: "20fall",
            },
            {
              name: "cs4560",
              term: "20fall",
            },
          ]
    }

    //props like parameters 
    //render like a fucntion and it returns the whole object to render on the website
    render() {
        return (
            <div className="container">
            <h1>
              Course List (For {this.props.instructer} at {this.props.term}){" "}
            </h1>
            <table className="table">
              {
                this.state.course.map((item) => (
                  <CourseRoleComponent item={item} />
                )) //iterate the elememnt
              }
            </table>
          </div>
        )
    }

}

export default CourseListComponent;
