import React from "react";
import CourseRoleComponent from "./CourseRowComponent";

class CourseListComponent extends React.Component {
  // state just like fields, local state instance
  state = {
    courses: [
      {
        id: "123",
        name: "cs5610",
        term: "20fall",
      },
      {
        id: "124",
        name: "cs5610",
        term: "20fall",
      },
      {
        id: "123333",
        name: "cs5610",
        term: "20fall",
      },
    ],
  };

  // function
  deleteCourse = (course) => {
    //update the state and re-render
    this.setState((prevState) => {
      return {
        courses: prevState.courses.filter((d) => d.id !== course.id) //update
      };
    });
  };

  //props like parameters
  //render like a fucntion and it returns the whole object to render on the website
  render() {
    return (
      <div className="container">
        <h1>
          Course List (For {this.props.instructer} at {this.props.term})
        </h1>
        <table className="table">
          {
            this.state.courses.map(item => (
              <tr>
                <td>
                  <CourseRoleComponent item={item} />
                </td>
                <td>{item.name}</td>
                <td>
                  <button
                    onClick={() => this.deleteCourse(item)} //careful!!!
                    className="btn btn-info">
                    Delete
                  </button>
                </td>
              </tr>
            ))
            //iterate the elememnt
          }
        </table>
      </div>
    );
  }
}

export default CourseListComponent;
