import React from "react";
import { Link } from "react-router-dom";
import CourseService from "../services/CourseService";
import Image from "../resources/images/logo.JPG";

class CourseCardComponent extends React.Component {
  state = {
    isEditing: false,
    course: this.props.course,
  };

  render() {
    let { deleteCourse } = this.props;
    let course = this.state.course;
    return (
      <div
        className={`col-12 col-sm-6 col-md-4 col-lg-3 col-xl-2 courseCard ${
          this.props.isSelected ? "highlighted" : ""
        }`}
        onClick={this.props.onClick}>
        <div>
          {this.state.isEditing ? (
            <input
              className="form-control"
              onChange={(e) => {
                const newTitle = e.target.value;
                this.setState((prevState) => ({
                  course: {
                    ...prevState.course,
                    title: newTitle,
                  },
                }));
              }}
              value={course.title}
            />
          ) : (
            <Link to={`/edit/${course._id}`}>{course.title}</Link>
          )}
        </div>
        <div> {course.modified} </div>
        <Image/>
        <div>
          <button
            onClick={() => deleteCourse(course)}
            className="btn btn-danger">
            Delete
          </button>
          {this.state.isEditing ? (
            <button
              onClick={() => {
                CourseService.updateCourse(course._id, course).then((_) =>
                  this.setState({
                    isEditing: false,
                  })
                );
              }}
              className="btn btn-success">
              Save
            </button>
          ) : (
            <button
              onClick={() => {
                this.setState({
                  isEditing: true,
                });
              }}
              className="btn btn-primary">
              Edit
            </button>
          )}
        </div>
      </div>
    );
  }
}

export default CourseCardComponent;
