import React from "react";
import CourseRoleComponent from "./CourseRowComponent";

class CourseListComponent extends React.Component {
  // state just like fields, local state instance
  state = {
    courses: [
      {
        id: "123",
        name: "cs5610",
        term: "20fall",
      },
      {
        id: "124",
        name: "cs5610",
        term: "20fall",
      },
      {
        id: "123333",
        name: "cs5610",
        term: "20fall",
      },
    ],
  };

  // delete course
  deleteCourse = (course) => {
    //update the state and re-render
    this.setState((prevState) => {
      return {
        courses: prevState.courses.filter(d => d.id !== course.id) //update
      };
    });
  };

  //add course
  addCourse = () => {
    const newCourse = {
      id: "566",
      name: "New Course",
      term: "fall2020",
    }

    this.setState((prevState) => {
      return {
        courses: [
          ...prevState.courses, newCourse
        ]
      };
    });
    this.setState( prevState => ({
        courses: [
          ...prevState.courses, newCourse
        ]
    })
    )}

  //props like parameters
  //render like a fucntion and it returns the whole object to render on the website
  render() {
    return (
      <div className="container">
        <h1>
          Course List (For {this.props.instructer} at {this.props.term})
        </h1>
        <table className="table">
          {
            this.state.courses.map(item => (
              <CourseRoleComponent 
              deleteCourse={this.deleteCourse}
              course={item} />
            ))
            //iterate the elememnt
          }
        </table>
        <button 
        onClick={() => this.addCourse}
        className="btn btn-primary">
        Add Course</button>
      </div>
    );
  }
}

export default CourseListComponent;
