import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import "../css/course-list.style.css";
import {Navbar,Button, Nav} from "react-bootstrap";
import {createCourse} from "../services/CourseService";
import { TiTimes, } from 'react-icons/ti';
import lessonService from '../services/LessonService';
import connet from 're'

class LessonTabs extends Component {

    //add course
    addCourse = () => {
        let newCourse = {
        title: "New Course",
        owner: "me",
        modified: "1/1/2020"
        }
        createCourse(newCourse)
        .then(newCourse => this.setState((prevState) => {
            return {
            courses: [
                ...prevState.courses, newCourse
            ]
            }
        }))
    }

    render() { 
        return (
                <div className="container">
                    <Navbar bg="light" variant="light">
                        <TiTimes className="topIcon"></TiTimes>
                        <Navbar.Brand href="#home" >{this.props.course.title}</Navbar.Brand>
                        <Navbar className="mr-auto active hidden-xs hidden-sm">
                            <Nav.Link href="#" >Build</Nav.Link>
                            <Nav.Link href="#" >Pages</Nav.Link>
                            <Nav.Link href="#">Theme</Nav.Link>
                            <Nav.Link href="#">Store</Nav.Link>
                            <Nav.Link href="#">APPs</Nav.Link>
                            <Nav.Link href="#">Settings</Nav.Link>
                        </Navbar>
                        <Button 
                        onClick={this.addCourse} className="btn btn-info"
                        >+</Button>
                    </Navbar>              
                </div> 

          );
    }
}

const LessonTabs1 = (
  {
    course,
    moduleId,
    lessons=[],
    createLessonForModule,
    deleteLesson,
    updateLesson
  }) =>
  <div>
    <h1>Lessons ({moduleId})</h1>
    <ul className="nav nav-tabs">
      {
        lessons.map(lesson =>
          <li key={lesson._id} className="nav-item">
            <Link to={`/edit/${course._id}/modules/${moduleId}/lessons/${lesson._id}`} className="nav-link">
            <button onClick={() => deleteLesson(lesson._id)}>
              <i className="fa fa-times"></i>
            </button>

              {
                !lesson.editing &&
                  <span>
                    <button onClick={() =>
                      updateLesson({...lesson, editing: true})
                    }>
                <i className="fa fa-pencil"></i>
              </button>
                    {lesson.title}
                  </span>
              }
              {
                lesson.editing &&
                  <span>
              <button onClick={() =>
                updateLesson({...lesson, editing: false})}>
                <i className="fa fa-check"></i>
              </button>
                    <input value={lesson.title}/>
                  </span>
              }
            </Link>
          </li>
        )
      }
    </ul>
    <button onClick={() => createLessonForModule(moduleId)}>
      Create
    </button>
  </div>

  const stateToPropertyMapper = (state) => ({
  course: state.courseReducer.course,
  lessons: state.lessonReducer.lessons,
  moduleId: state.lessonReducer.moduleId
})

const dispatchToPropertyMapper = (dispatch) => ({
  updateLesson: (newLesson) =>
    lessonService.updateLesson(newLesson)
      .then(actuaLesson => dispatch({
        type: "UPDATE_LESSON",
        lesson: actuaLesson
      })),
  deleteLesson: (lessonId) =>
    lessonService.deleteLesson(lessonId)
      .then(status => dispatch({
        type: "DELETE_LESSON",
        lessonId
      })),
  createLessonForModule: (moduleId) =>
    lessonService.createLessonForModule(
      moduleId, {
      title: "New Lesson"
    })
      .then(actualLesson => dispatch({
        type: "CREATE_LESSON_FOR_MODULE",
        lesson: actualLesson
      }))
})

export default connect
(stateToPropertyMapper,
  dispatchToPropertyMapper)
(LessonTabs)
//export default LessonTabs;
