import React, { Component } from 'react';
import { TiTimes,TiEdit } from 'react-icons/ti';
import CourseEditorComponent from './CourseEditorComponent';

export const ModuleListComponent = ({
        //want to receive data from reducer
        course = {},
        modules = [],
        deleteModule,
        createModule,
        updateModule
}) => 
        <div>
                <h1>Modules for {course.name}</h1>
                <ul className="list-group wbdv-module-item">
                        {
                        modules.map(module => 
                                <li className="list-group-item" key={module._id}>
                                        <i onClick={() => deleteModule(module)}><TiTimes/></i>  
                                        <i onClick={() => updateModule(module)}><TiEdit/></i>    
                                        {module.title}    
                                </li>)
                        }
                </ul>
                <i className="float-right" 
                //create module for particular course
                onClick={() => createModule(course)}
                >+</i>

        </div>

class ModuleList extends Component {
    render() { 
        return (
            <div className="wbdv-module-list col-sm-3">
                <div>
                    <ul className="list-group wbdv-module-item">
                        <li className="list-group-item wbdv-module-item-title">Module1-JQuery 
                        <i className="float-right"><TiTimes/></i></li>
                        <li className="list-group-item active wbdv-module-item-title">Module2-React<i
                                className="fa fa-times float-right wbdv-module-item-delete-btn"><TiTimes/></i>
                        </li>
                        <li className="list-group-item wbdv-module-item-title">Module2-Redux <i
                                className="float-right wbdv-module-item-delete-btn"><TiTimes/></i>
                        </li>
                        <li className="list-group-item wbdv-module-item-title">Module3-Native <i
                                className="fa fa-times float-right wbdv-module-item-delete-btn">
                                <TiTimes/></i></li>
                        <li className="list-group-item wbdv-module-item-title">Module4-Angular <i
                                className="fa fa-times float-right wbdv-module-item-delete-btn">
                                <TiTimes/></i></li>
                        <li className="list-group-item wbdv-module-item-title">Module5-Node.js <i
                                className="fa fa-times float-right wbdv-module-item-delete-btn">
                                <TiTimes/></i></li>
                        <li className="list-group-item wbdv-module-item-title">Module6-Express <i
                                className="fa fa-times float-right wbdv-module-item-delete-btn">
                                <TiTimes/></i></li>
                        <li className="list-group-item wbdv-module-item-add-btn"> 
                        <i className="float-right">+</i></li>
                    </ul>
                </div>
            </div>
          );
    }
}

export default ModuleList;

const stateToPropertyMapper = (state) => ({
        modules: state.moduleReducer.modules
})
