import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import "../css/course-list.style.css";
import lessonService from '../services/LessonService';
import {connect} from 'react-redux';
import { TiTimes,TiEdit,TiInputChecked } from 'react-icons/ti';

const LessonTabs = (
  {
    course,
    moduleId,
    lessons=[],
    createLessonForModule,
    deleteLesson,
    updateLesson
  }) =>
  <div>
    <h1>Lessons ({moduleId})</h1>
    <ul className="nav nav-tabs">
      {
        lessons.map(lesson =>
          <li key={lesson._id} className="nav-item">
            <Link to={`/edit/${course._id}/modules/${moduleId}/lessons/${lesson._id}`} className="nav-link">

              <i onClick={() => deleteLesson(lesson._id)}> <TiTimes/></i>
              {
                !lesson.editing &&
                  <span>
                    <i ><TiInputChecked onClick={() =>
                      updateLesson({...lesson, editing: true})
                    }/>
                    </i>
                    {lesson.title}
                  </span>
              }
              {
                lesson.editing &&
                  <span>
              <button onClick={() =>
                updateLesson({...lesson, editing: false})}>
                <i className="fa fa-check"></i>
              </button>
                    <input value={lesson.title}/>
                  </span>
              }
            </Link>
          </li>
        )
      }
    </ul>
    <button onClick={() => createLessonForModule(moduleId)}>
      Create
    </button>
  </div>

  const stateToPropertyMapper = (state) => ({
  course: state.courseReducer.course,
  lessons: state.lessonReducer.lessons,
  moduleId: state.lessonReducer.moduleId
})

const dispatchToPropertyMapper = (dispatch) => ({
  updateLesson: (newLesson) =>
    lessonService.updateLesson(newLesson)
      .then(actuaLesson => dispatch({
        type: "UPDATE_LESSON",
        lesson: actuaLesson
      })),
  deleteLesson: (lessonId) =>
    lessonService.deleteLesson(lessonId)
      .then(status => dispatch({
        type: "DELETE_LESSON",
        lessonId
      })),
  createLessonForModule: (moduleId) =>
    lessonService.createLessonForModule(
      moduleId, {
      title: "New Lesson"
    })
      .then(actualLesson => dispatch({
        type: "CREATE_LESSON_FOR_MODULE",
        lesson: actualLesson
      }))
})

export default connect
(stateToPropertyMapper,
  dispatchToPropertyMapper)
(LessonTabs)
