import React from "react";
import TopicPills from "../components/TopicPills";
import { FaToggleOff,FaTrashAlt } from 'react-icons/fa';
import { ImArrowUp2,  ImArrowDown2} from 'react-icons/im';
import { IoMdAddCircleOutline} from 'react-icons/io';
import LessonTabs from "../components/LessonTabsComponent";
import WidgetListComponent from "../components/WidgetListComponent";
import ModuleListComponent from "../components/ModuleListComponent";
import { connect } from "react-redux";
import {findCourseById} from '../services/CourseService'
import moduleService from "../services/ModuleService"
import lessonService from "../services/LessonService"
import LessonNavBar from "../components/LessonNavBarComponent";
class CourseEditorComponent extends React.Component {

    componentDidMount() {
        const courseId = this.props.match.params.courseId;
        const moduleId = this.props.match.params.moduleId;
        const lessonId = this.props.match.params.lessonId;
        this.props.findCourseById(courseId);
        this.props.findModulesForCourse(courseId);
        if (moduleId) {
            this.props.findLessonsForModule(moduleId);
        } 
        this.props
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        const moduleId = this.props.match.params.moduleId;
        if (prevProps.match.params.moduleId !== moduleId) {
            //avoid infinite loop
            if (moduleId) {
                    this.props.findLessonsForModule(moduleId);
                } 
        }
    }

    render() {
        return(
            <div>
                <div className="container wbdv-course-editer">
                    <div className="row"> 
                    <LessonNavBar course={this.props.course}/>
                    </div>

                    <div className="row">
                        <ModuleListComponent/>
                        <div className="col-sm-8">
                        <br/>
                        <LessonTabs  course={this.props.course}/>
                        <br/>
                        <TopicPills/>
                            <div className="wbdv-light-gray-border">
                                <span className="pull-right float-right">
                                    <a href="#" className="btn btn-sm btn-success">save</a>
                                    <a href="#">Preview</a>
                                    <a href="#" className="btn">
                                        <FaToggleOff/>
                                    </a>
                                </span>
                                <br/>
                                <br/>

                                <h3> Heading Widget
                                    <span className="float-right">
                                    <a href="#" className="btn btn-sm btn-warning">
                                        <ImArrowUp2/>
                                    </a>
                                    <a href="#" className="btn btn-sm btn-warning">
                                        <ImArrowDown2/>
                                    </a>
                                    <select>
                                        <option>Heading</option>
                                        <option>YouTube</option>
                                        <option>Slides</option>
                                        <option>Image</option>
                                        <option>List</option>
                                    </select>
                                    <a href="#" className="btn btn-sm btn-danger">
                                        <FaTrashAlt/>
                                    </a>
                                    </span>
                                </h3>
                                <input className="form-control" placeholder="Heading Text"/>
                                <br/>
                                <select className="form-control">
                                    <option>Heading 1</option>
                                    <option>Heading 2</option>
                                    <option>Heading 3</option>
                                    <option>Heading 4</option>
                                    <option>Heading 5</option>
                                    <option>Heading 6</option>
                                </select>
                                <br/>
                                <h4>Preview</h4>
                                <input className="form-control" placeholder="Heading Text"/>
                                <i className="float-right btn btn-md btn-danger" aria-hidden="true">
                                <IoMdAddCircleOutline/>
                                </i>
                            </div>
                            <WidgetListComponent/>
                        </div>
                    </div>
                </div>
        </div>
        )
    }    
}

const stateToPropertyMapper = (state) => ({
    course: state.courseReducer.course,
    lessons:state.lessonReducer.lessons
})

const propertyToDispatchMapper = (dispatch) => ({
    findCourseById: (courseId) => findCourseById(courseId)
        .then(actualCourse => dispatch({
            type: "SET_COURSES",
            course: actualCourse
        })),
    findModulesForCourse: (courseId) => moduleService.findModulesForCourse(courseId)
        .then(actualModule => dispatch({
            type: "FIND_MODLULES_FOR_COURSE",
            modules: actualModule
        })),
    findLessonsForModule: (moduleId) => lessonService.findLessonsForModule(moduleId)
        .then(actualLesson => dispatch({
            type: "FIND_LESSONS_FOR_MODULE",
            moduleId,
            lessons: actualLesson
        }))
})

export default connect(stateToPropertyMapper,propertyToDispatchMapper)(CourseEditorComponent)
