import React from "react";
import CourseRoleComponent from "./CourseRowComponent";
import {findAllCourses} from "../services/CourseService";

class CourseListComponent extends React.Component {
  // state just like fields, local state instance
  state = {
    courses: []
  }

  //initialize
  componentDidMount() {
    findAllCourses()
      .then(courses => {
        this.setState(prevState => {
          return {
            courses : courses
          }
        })
      })
  }

  // delete course
  deleteCourse = (course) => {
    //update the state and re-render
    this.setState((prevState) => {
      return {
        courses: prevState.courses.filter(d => d._id !== course._id) //update
      };
    });
  };

  //add course
  addCourse = (course) => {
    let newCourse = {
      _id: "566",
      title: "New Course",
      lastUpdated: "fall2020",
    }

    this.setState((prevState) => {
      return {
        courses: [
          ...prevState.courses, newCourse
        ]
      }
    })
  }

  //props like parameters
  //render like a fucntion and it returns the whole object to render on the website
  render() {
    return (
      <div className="container">
        <h1>
          Course List (For {this.props.instructer} at {this.props.term})
        </h1>
        <table className="table">
          {
            this.state.courses.map(item => (
              <CourseRoleComponent 
              deleteCourse={this.deleteCourse}
              course={item} />
            ))
            //iterate the elememnt
          }
        </table>
        <button 
        onClick={this.addCourse}
        className="btn btn-primary">
        Add Course</button>
      </div>
    );
  }
}

export default CourseListComponent;
