import React from "react";
import {Link, Route} from "react-router-dom";

const courseBeingEdited = false
const editCourse = () => {}
const CourseRowComponent = ({course, deleteCourse,}) => (
  <tr>
    <td>
    {
      course === courseBeingEdited && 
      <input value={courseBeingEdited.title} className="form-control"></input>
    }
    {
      course !== courseBeingEdited && 
      <Link to={`/edit/${course._id}`}>{course.title}</Link>
    }
    </td>
    <td>{course.owner}</td>
    <td>{course.lastUpdated}</td>
    <td>
      <button
        onClick={() => deleteCourse(course)} //careful!!!
        className="btn btn-success">
        Delete
      </button>
      <button 
      onClick={() => editCourse(course)}
      className="btn btn-info">Edit</button>
    </td>
  </tr>
);

export default CourseRowComponent;
