import {DELETE_WIDGET, CREATE_WIDGET, UPDATE_WIDGET} from "../actions/moduleActions"
const initialState = {
    modules: [
        {
            _id: "123",
            title: "module 1",
            editing:false           
        },
        {
            _id: "234",
            title: "module 2",
            editing:true                  
        },
        {
            _id: "456",
            title: "module 3",
            editing:false               
        }
    ]
}

const moduleReducer = (state = initialState, action) => {
        switch (action.type) {
        case CREATE_MODULE:
            return {
                modules: [...state.modules,{
                    _id: Date.now()+ "",
                    name: "new module"
                }]
            }
        case UPDATE_MODULE:
            return {
                modules: state.modules.map(module => module._id === action.module._id ? action.module : module)
            }
        case DELETE_MODULE:
            return {
                modules: state.modules.filter(module => module._id !== action.module._id)
            }
        default:
            return state
    }
}
