import React from "react";
import CourseRoleComponent from "./CourseRowComponent";
import {findAllCourses,deleteCourse,createCourse} from "../services/CourseService";
import { FaSortAlphaDown } from 'react-icons/fa';
import { BsFillGrid3X3GapFill } from 'react-icons/bs';
import Table from 'react-bootstrap/Table'

class CourseTable extends React.Component {
  // state just like fields, local state instance
  state = {
    courses: this.props.courses,
    courseBeingEdited: {}
  }

  //initialize
  componentDidMount() {
    findAllCourses()
      .then(courses => {
        this.setState({
            courses : courses
          }
        )
      })
  }

  // delete course
  deleteCourse = (course) => {
    //update the state and re-render
    deleteCourse(course._id)
      .then(this.setState(prevState => ({
        courses: prevState.courses.filter(d => d._id !== course._id) //update
      })))
  }

  //add course
  addCourse = (course) => {
    let newCourse = {
      title: "New Course",
      owner: "me",
    }
    createCourse(newCourse)
      .then(newCourse => this.setState((prevState) => {
        return {
          courses: [
            ...prevState.courses, newCourse
          ]
        }
    }))
  }

  //edit course
  editCourse = (course) => {
    this.setState({
      courseBeingEdited: course
    })
  }

  //props like parameters
  //render like a fucntion and it returns the whole object to render on the website
  render() {
    return (
      <div className="container">
        <Table hover>
          <thead>
              <tr>
                <th>Title</th>
                <th>Owned by</th>
                <th></th>
                <th>
                  <FaSortAlphaDown/>
                  <BsFillGrid3X3GapFill/></th>
              </tr>
            </thead>
          {
            this.state.courses.map(item => (
              <CourseRoleComponent 
              deleteCourse={this.deleteCourse}
              course={item} />
            ))
            //iterate the elememnt
          }
        </Table>
        <button 
        onClick={this.addCourse}
        className="btn btn-primary">
        Add Course</button>
      </div>
    );
  }
}

export default CourseTable;
