import React, { Component } from 'react';
import {Link} from 'react-router-dom';
import "../css/course-list.style.css";
import topicService from '../services/TopicService';
import {connect} from 'react-redux';
import { TiTimes,TiPencil } from 'react-icons/ti';
import {HiCheck} from 'react-icons/hi'
import { FaPlus } from 'react-icons/fa';

const TopicPills = (
  {
    course,
    moduleId,
    lessonId,
    topics=[],
    createTopicForLesson,
    deleteTopic,
    updateTopic,
  }) =>
  <div>
    <ul className="nav nav-tabs">
      {
        lessons.map(topic =>
          <li key={topic._id} className="nav-item">
            <a className="nav-link">
            <Link to={`/edit/${course._id}/modules/${moduleId}/lessons/${lesson._id}/topics`}>
              {
                !lesson.editing &&
                <span>
                {lesson.title}
                <TiPencil onClick={() => updateTopic({...lesson, editing: false})}/>
                </span>
              }
              {
                lesson.editing &&
                <span>
                  <input value={lesson.title}/>
                  <TiTimes onClick={() => deleteTopic(lesson._id)}/>  
                  <HiCheck onClick={() => updateTopic({...lesson, editing: true})}/>
                </span>
              }
            </Link>
            </a>
          </li>
        )
      }
      <button className="btn btn-sm" onClick={() => createTopicForLesson(lessonId)}>
        <FaPlus/>
      </button>      
    </ul>

  </div>

  const stateToPropertyMapper = (state) => ({
  course: state.courseReducer.course,
  lessons: state.lessonReducer.lessons,
  moduleId: state.lessonReducer.moduleId
})

const dispatchToPropertyMapper = (dispatch) => ({
  updateTopic: (newTopic) =>
    topicService.updateTopic(newTopic)
      .then(actualTopic => dispatch({
        type: "UPDATE_TOPIC",
        lesson: actualTopic
      })),
  deleteTopic: (lessonId) =>
    topicService.deleteTopic(lessonId)
      .then(status => dispatch({
        type: "DELETE_TOPIC",
        lessonId
      })),
  createTopicForLesson: (lessonId) =>
    topicService.createTopicForLesson(
      lessonId, {
      title: "New Topic",
      editing: false
    })
      .then(actualTopic => dispatch({
        type: "CREATE_TOPIC_FOR_LESSON",
        lesson: actualTopic
      }))
})

export default connect
(stateToPropertyMapper,
dispatchToPropertyMapper)
(TopicPills)
