import ReactDOM from 'react-dom'
import React, { Component } from 'react';
import { Button } from 'react-bootstrap';

 class Toggle extends Component {
    constructor(props) {
         super(props);
         this.state = {value: true}
     }

    handleClick = (value) => {
        if (value === true) {
            this.setState({
                value : !this.state.value
            })
            return <h1> this is the first messgae</h1>
        }
        if (value === false) {
            this.setState({
                value : !this.state.value
            })
            return <h1>This is the seconde message</h1>
        }

    }

    render() { 
        return ( 
            <React.Fragment>
            <Button className="btn btn-primary"
            onClick={() => this.handleClick(this.state.value)}> Toggle </Button>
            </React.Fragment>
         );
    }
 }
export default Toggle;
