import React from "react";
import {Link, Route} from "react-router-dom";
import {updateCourse} from "../services/CourseService";

const courseBeingEdited = false
const editCourse = () => {}
export default class CourseRowComponent extends React.Component{
  state = {
    editing: false,
    course: this.props.course
  }
  render() {
    return(
        <tr>
        <td>
        {
          this.state.editing && 
          <input 
          className="form-control"
          onChange={
            (event) => {
              const newTitle = event.target.value
              this.setState(prevState => ({
                course: {...prevState.course, title: newTitle}
              }))
            }
          }
          value={this.state.course.title} className="form-control"></input>
        }
        {
          !this.state.editing && 
          <Link to={`/edit/${this.state.course._id}`}>{this.state.course.title}</Link>
        }
        </td>
        <td>{this.props.course.owner}</td>
        <td>{this.props.course.lastUpdated}</td>
        <td>
          <button
            onClick={() => this.props.deleteCourse(this.props.course)} //careful!!!
            className="btn btn-success">
            Delete
          </button>
          {
            !this.state.editing &&
              <button 
          onClick={() => this.setState({editing : true})}
          className="btn btn-info">Edit</button>
          }

          {
            this.state.editing &&
            <button
              onClick={() => {
                updateCourse(this.state.course._id, this.state.course)
                  .then(status => this.setState({editing: false}))
              }}
              className="btn btn-primary">OK</button>
          }
        </td>
      </tr>
    )
  }

}
