import React, { Component } from 'react';
import { TiTimes,TiEdit,TiInputChecked } from 'react-icons/ti';
import { connect } from 'react-redux';
import {deleteModule, updateModule, createModule, editModule, okModule} from "../actions/moduleActions"

const ModuleListComponent = ({
        //want to receive data from reducer
        course = {},
        modules = [],
        deleteModule,
        createModule,
        updateModule
}) => 
        <div className="col-sm-4">
        <h1>Modules for </h1>
        <div className="wbdv-module-list">
                <div>
                        <ul className="list-group wbdv-module-item">
                        {
                        modules.map(module => 
                                <li className="list-group-item" key={module._id}>

                                        <span className="float-right">
                                                <i onClick={() => deleteModule(module)}><TiTimes/></i>
                                                {module.editing &&
                                                <div>
                                                        <input value={module.title}
                                                        onChange={(event) => updateModule({
                                                                ...module,
                                                                title:event.target.value
                                                        })}/>
                                                        <i onClick={() => okModule(module)}><TiInputChecked/></i>
                                                </div>
                                                }
                                                {!module.editing &&
                                                        <span>
                                                                                        {module.title}
                                                        </span>
                                                }  
                                                <i onClick={() => updateModule(module)}><TiEdit/></i>                                         
                                        </span>

                                </li>)
                        }
                        </ul>
                </div>
        </div>
        <i className="float-right" 
        //create module for particular course
        onClick={() => createModule(course)}
        >+</i>
        </div>

const propertyToDispatchMapper = (dispatch) => ({
    deleteModule: (module) => deleteModule(dispatch, module),
    createModule: () => createModule(dispatch),
    updateModule: (module) => updateModule(dispatch, module),
    editModule: (module) => editModule(dispatch, module),
    okModule: (module) => okModule(dispatch, module),
 })

const stateToPropertyMapper = (state) => ({
        modules: state.moduleReducer.modules
})

export default connect
        (stateToPropertyMapper,propertyToDispatchMapper)
        (ModuleListComponent)
