const initialState = {
    widgets: [
        {
            _id: "123",
            name: "Widget 1"            
        },
        {
            _id: "234",
            name: "Widget 2"            
        },
        {
            _id: "456",
            name: "Widget 3"            
        }

    ]
}

const widgetReducer = (state = initialState, action) => {
    switch (action.type) {
        case "CREATE_WIDGET":
            return {
                widgets: []
            }
        case "UPDATE_WIDGET":
        case "DELETE_WIDGET":
            return {
                widgets: state.widgets.filter(widget => widget._id !== action.widget._id)
            }
        default:
            return state
    }
}

export default widgetReducer;
