import { render } from '@testing-library/react';
import React from 'react';
import {connect} from "react-redux";
import {deleteWidget, createWidget, updateWidget, editWidget} from "../actions/widgetActions";

const WidgetList = ({
    widgets=[], 
    createWidget, 
    deleteWidget, 
    updateWidget}) => 
        <div>
            <h1>Widgets!!!!</h1>
            <ul>
                {
                    widgets.map(widget =>
                        <li>
                            <button onClick={() => deleteWidget(widget)} className="btn btn-sm btn-primary">
                                Delete
                            </button>
                            <button onClick={() => editWidget(widget)} className="btn btn-sm btn-info">
                                Edit
                            </button>
                            {widget.editing && 
                            <input value={widget.name}
                                    onChange={(event) => updateWidget({
                                        ...widget,
                                        name:event.target.value
                                    })}/>                            
                            }
                            {!widget.editing}
                            {widget.name}

                        </li>
                    )
                }
            </ul>
            <button className="btn btn-sm btn-danger" onClick={createWidget}>+</button>
        </div>

 const propertyToDispatchMapper = (dispatch) => ({
    deleteWidget: (widget) => deleteWidget(dispatch, widget),
    createWidget: () => createWidget(dispatch),
    updateWidget: (widget) => updateWidget(dispatch, widget),
    editWidget: (widget) => editWidget(dispatch, widget)
 })

 const stateToPropertyMapper = (state) => ({
    widgets: state.widgetReducer.widgets
})

export default connect
    (stateToPropertyMapper, propertyToDispatchMapper)
    (WidgetList)
